// ✅ todo import react 
// ✅ todo import the API call
// ✅ todo import component styles 
import React from "react";
//import API from "../../utils/api.js"
import Search from "../Search"
import "./style.css";
import Robots from "../robots.json"

// ✅ todo set up state 
class Table extends React.Component {

  state = {
    search: "",
    sortOrder: "",
    robots: []
  }

  //todo handle the search input 
  handleInputChange = event => {

    if (event.target.name === "search") {
      const searchTerm = event.target.value.toLowerCase();
      this.setState({
        search: searchTerm
      })
    }
  }

  //todo Sort by name 
  sortName = () => {
    const sortNames = this.state.robots.sort((a, b) => {
      if (b.fname > a.fname) {
        return -1
      }
      if (a.fname > b.fname) {
        return 1
      }
      return 0;
    });

    if (this.state.sortOrder === "DESC") {
      sortNames.reverse();
      this.setState({ sortOrder: "ASC" });
    } else {
      this.setState({ sortOrder: "DESC" });
    }
    this.setState({ results: sortNames })
  }


  //todo render the table 
  render() {
    return (
      <div>
        <Search handleInputChange={this.handleInputChange}
          search={this.state.search} />

        <div>
        <table className="table">
            <thead>
              <tr>
                <th>Image</th>
                <th><a href="" onClick={this.sortName}><i className="fal fa-sort-circle me-3"></i>Name</a></th>

              </tr>
            </thead>

            { // Sort by name
              this.state.robots && this.state.robots.map(item =>
                item.fname.toLowerCase().includes(this.state.search) ?
                  <tbody>
                    <tr>
                      <td ><img src={item.image} className="avatar" alt="employee"/></td>
                      <td >{item.fname}</td>
                    </tr>
                  </tbody>
                  :
                    null
              )}
          </table>
        </div>
      </div>
    )
  }
}

export default Table;
